files.content:
body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 0;
    background-color: #f4f4f4;
}
header {
    background-color: #333;
    color: #fff;
    padding: 1rem;
    text-align: center;
}
nav ul {
    list-style: none;
    padding: 0;
}
nav ul li {
    display: inline;
    margin: 0 1rem;
}
nav ul li a {
    color: #fff;
    text-decoration: none;
}
section {
    padding: 2rem;
    margin: 1rem 0;
    background-color: #fff;
}
form {
    display: flex;
    flex-direction: column;
}
form label {
    margin-top: 1rem;
}
form input, form textarea {
    padding: 0.5rem;
    margin-top: 0.5rem;
}
form button {
    margin-top: 1rem;
    padding: 0.5rem;
    background-color: #333;
    color: #fff;
    border: none;
    cursor: pointer;
}
form button:hover {
    background-color: #555;
}

document.addEventListener('DOMContentLoaded', function() {
    const projects = [
        { name: 'Project 1', description: 'An interactive web application built with React.' },
        { name: 'Project 2', description: 'A responsive portfolio website using HTML, CSS, and JavaScript.' },
        { name: 'Project 3', description: 'A backend API built with Node.js and Express.' }
    ];

    const projectsList = document.getElementById('projects-list');
    projects.forEach(project => {
        const projectDiv = document.createElement('div');
        projectDiv.classList.add('project');
        projectDiv.innerHTML = `<h3>${project.name}</h3><p>${project.description}</p>`;
        projectsList.appendChild(projectDiv);
    });

    const contactForm = document.getElementById('contact-form');
    contactForm.addEventListener('submit', function(event) {
        event.preventDefault();
        alert('Thank you for your message!');
        contactForm.reset();
    });
});
